Prism.plugins.toolbar.registerButton('select-code', function() {var button = document.createElement('button'); button.innerHTML = 'Select Code'; button.addEventListener('click', function () {if (document.body.createTextRange) {var range = document.body.createTextRange(); range.moveToElementText(env.element); range.select(); } else if (window.getSelection) {var selection = window.getSelection(); var range = document.createRange(); range.selectNodeContents(env.element); selection.removeAllRanges(); selection.addRange(range); } }); return button; });